export interface ShowToastOption {
    content: string;
    type?: 'success' | 'fail' | 'none';
    duration?: number;
    success?: () => any;
    fail?: (res: any) => any;
    complete?: (res?: any) => any;
}
export interface HideToastOption {
    success?: () => any;
    fail?: (res: any) => any;
    complete?: (res?: any) => any;
}
export interface WebQueueOption {
    content: string;
    duration?: number;
    icon?: 'success' | 'fail' | 'none';
    success?: () => void;
    fail?: (res: any) => void;
    complete?: (res?: any) => void;
}
